import React from 'react'
import { marked } from 'marked';

export default function Previewer(props) {
    function parseText() {
        return (
            <div>
                {marked.parse(props.text)}
            </div>
            
        )
    }

    return (
        <section id="preview-section">
            <h2>Preview</h2>
            {parseText()}
        </section>
    )
}
