import React from 'react'
import Previewer from './Previewer';
import placeholderText from './placeholderText.txt'
import {defaultText} from './defaultText'


export default function Editor() {
    const [input, setInput] = React.useState(defaultText);

    /*
    React.useEffect(() => {
        function iniPlaceholder() {
            fetch(placeholderText)
                .then(response => response.text())
                .then((data) => setInput(data))
        }

        iniPlaceholder();
    }, [])*/

    function handleChange(event) {
        setInput(event.target.value)
    }

    return (
        <div id="main-content">
            <section id="editor-section">
                <h2 className="section-title">Editor</h2>
                <textarea id="editor" onChange={handleChange} defaultValue={input} />
            </section>
            <Previewer text={input} />
        </div>
    )
}
