import React from 'react'
import { marked } from 'marked';
import Parser from 'html-react-parser';

export default function Previewer(props) {
    function parseText() {
        // Parse to marked
        //var text = marked.parse(props.text)
        var text = props.text;

        // Identify escape characters
        const regex = /\\/;

        const replacement = /\\\w/g;

        console.log(replacement.exec(text))

        if (regex.test(text)) {
            console.log(text)
        }

        return (
            <div>
                {Parser(text)}
            </div>
        )
    }

    return (
        <section id="preview-section">
            <h2>Preview</h2>
            {parseText()}
        </section>
    )
}
